import React from 'react';
import {
  Chart as ChartJS,
  TimeScale,
  LinearScale,
  Tooltip,
  Legend,
} from 'chart.js';
import 'chartjs-adapter-date-fns'; // date-fns adapter'ını ekleyin
import { CandlestickController, CandlestickElement } from 'chartjs-chart-financial'; // financial chart bileşenleri

// ChartJS'i kaydediyoruz
ChartJS.register(
  TimeScale,
  LinearScale,
  CandlestickController,
  CandlestickElement,
  Tooltip,
  Legend
);

export const CandleChart = ({ priceData, interval, onIntervalChange }) => {
  const data = {
    datasets: [
      {
        label: 'DOGE/USDT',
        data: priceData, // OHLC verisi (open, high, low, close) içeren veriler
        borderColor: 'rgba(0, 0, 0, 1)', // Çizgi rengini belirliyoruz
        borderWidth: 1, // Çizgi kalınlığını ayarlıyoruz
        upColor: 'rgba(0, 255, 0, 1)', // Yükselen mumlar için renk
        downColor: 'rgba(255, 0, 0, 1)', // Düşen mumlar için renk
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: interval === '1d' ? 'day' : 'hour',
          tooltipFormat: 'yyyy-MM-dd HH:mm', // Zaman formatını ayarlıyoruz
        },
        title: {
          display: true,
          text: 'Time', // X ekseni için başlık
        },
      },
      y: {
        title: {
          display: true,
          text: 'Price (USDT)', // Y ekseni için başlık
        },
      },
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const ohlc = context.raw; // OHLC verisini alıyoruz
            return `O: ${ohlc.o}, H: ${ohlc.h}, L: ${ohlc.l}, C: ${ohlc.c}`; // Tooltip etiketini gösteriyoruz
          },
        },
      },
    },
  };

  return (
    <div>
      <div style={{ marginBottom: 10 }}>
        {['15m', '1h', '4h', '1d'].map((i) => (
          <button
            key={i}
            onClick={() => onIntervalChange(i)}
            style={{ marginRight: 8 }}
          >
            {i}
          </button>
        ))}
      </div>
      {/* Chart bileşenini render ediyoruz */}
      <ChartJS type="candlestick" data={data} options={options} />
    </div>
  );
};
