import React, { useState, useEffect } from "react";
import { WalletInfo } from "./components/WalletInfo";
import { Chart } from "./components/Chart";
import { TradeForm } from "./components/TradeForm";
import { OpenOrders } from "./components/OpenOrders";
import { OrderHistory } from "./components/OrderHistory";
import { useMexc } from './hooks/useMexc';



function App() {
  const {
    balance,
    currentPrice,
    priceHistory,
    openOrders,
    orderHistory,
    connectWallet,
    placeOrder,
    setLeverage,
    applyAlgorithm,
  } = useMexc();

  const [algorithm, setAlgorithm] = useState("meanReversion");
  const [leverage, setLeverageInput] = useState(10);

  useEffect(() => {
    setLeverage(leverage);
  }, [leverage, setLeverage]);

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 md:px-8 font-sans">
      <header className="text-center mb-12">
        <h1 className="text-4xl font-bold text-gray-800">Doge/USDT Trading Bot</h1>
        <p className="text-gray-500 mt-2 text-sm">Automated leverage trading with real-time data</p>
      </header>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
        {/* Left Column */}
        <div className="space-y-6">
          <div className="bg-white rounded-2xl shadow p-6">
            <WalletInfo balance={balance} connectWallet={connectWallet} />
          </div>
          <div className="bg-white rounded-2xl shadow p-6">
            <TradeForm
              currentPrice={currentPrice}
              placeOrder={placeOrder}
              algorithm={algorithm}
              setAlgorithm={setAlgorithm}
              leverage={leverage}
              setLeverageInput={setLeverageInput}
              applyAlgorithm={applyAlgorithm}
              connectWallet={connectWallet}
            />
          </div>
        </div>

        {/* Center Column - Full width on small screens, double on large */}
        <div className="lg:col-span-2 bg-white rounded-2xl shadow p-6">
          <Chart priceHistory={priceHistory} currentPrice={currentPrice} />
        </div>

        {/* Bottom Section - Full width */}
        <div className="lg:col-span-3 space-y-6">
          <div className="bg-white rounded-2xl shadow p-6">
            <OpenOrders orders={openOrders} currentPrice={currentPrice} />
          </div>
          <div className="bg-white rounded-2xl shadow p-6">
            <OrderHistory history={orderHistory} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
