import React from 'react';
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  Title,
  Tooltip,
  CategoryScale,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

// Chart bileşenlerini kaydet
ChartJS.register(LineElement, PointElement, LinearScale, Title, Tooltip, CategoryScale);

export const Chart = ({ priceHistory, currentPrice, loading }) => {
  const data = {
    labels: priceHistory.map((_, i) => i),
    datasets: [{
      label: 'DOGE/USDT Price',
      data: priceHistory,
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.1
    }]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { display: false },
      tooltip: {
        callbacks: {
          label: (context) => `Price: ${context.parsed.y.toFixed(8)} USDT`
        }
      }
    },
    scales: {
      y: {
        ticks: {
          callback: (value) => value.toFixed(8)
        }
      }
    }
  };

  return (
    <div className="chart-container">
      <h3>Current Price: {currentPrice.toFixed(8)} USDT</h3>
      <Line data={data} options={options} />
    </div>
  );
};
