import React from 'react';
import {
  Chart as ChartJS,
  TimeScale,
  LinearScale,
  CandlestickController,
  CandlestickElement,
  Tooltip,
  Legend
} from 'chart.js';
import { Chart } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

ChartJS.register(
  TimeScale,
  LinearScale,
  CandlestickController,
  CandlestickElement,
  Tooltip,
  Legend
);

export const CandleChart = ({ priceData, interval, onIntervalChange }) => {
  const data = {
    datasets: [{
      label: 'DOGE/USDT',
      data: priceData, // format: [{x: timestamp, o: 0.1, h: 0.12, l: 0.09, c: 0.11}, ...]
      borderColor: 'rgba(0, 0, 0, 1)',
      borderWidth: 1
    }]
  };

  const options = {
    responsive: true,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: interval === '1d' ? 'day' : 'hour',
          tooltipFormat: 'yyyy-MM-dd HH:mm',
        },
        title: {
          display: true,
          text: 'Time'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Price (USDT)'
        }
      }
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: context => {
            const ohlc = context.raw;
            return `O: ${ohlc.o}, H: ${ohlc.h}, L: ${ohlc.l}, C: ${ohlc.c}`;
          }
        }
      }
    }
  };

  return (
    <div>
      <div>
        <button onClick={() => onIntervalChange('15m')}>15m</button>
        <button onClick={() => onIntervalChange('1h')}>1h</button>
        <button onClick={() => onIntervalChange('4h')}>4h</button>
        <button onClick={() => onIntervalChange('1d')}>1d</button>
      </div>
      <Chart type='candlestick' data={data} options={options} />
    </div>
  );
};
