import axios from 'axios';

const API_URL = 'https://api.mexc.com';

// MEXC Public API Endpoint'leri
export const getDogeUsdtPrice = async () => {
  try {
    const response = await axios.get(`${API_URL}/api/v3/ticker/price?symbol=DOGEUSDT`);
    return parseFloat(response.data.price);
  } catch (error) {
    console.error('Error fetching DOGE price:', error);
    return 0;
  }
};

export const getPriceHistory = async () => {
  try {
    const response = await axios.get(`${API_URL}/api/v3/klines?symbol=DOGEUSDT&interval=1m&limit=100`);
    return response.data.map(k => parseFloat(k[4])); // Kapanış fiyatları
  } catch (error) {
    console.error('Error fetching price history:', error);
    return Array(100).fill(0.15); // Fallback data
  }
};
