import { useEffect, useRef, useState } from 'react';

export default function useMexcCandles(symbol = 'DOGEUSDT', interval = '1m') {
  const [candles, setCandles] = useState([]);
  const ws = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket('wss://wbs.mexc.com/ws');

    ws.current.onopen = () => {
      const payload = {
        method: "SUBSCRIPTION",
        params: [`kline_${symbol}_${interval}`],
        id: 1
      };
      ws.current.send(JSON.stringify(payload));
    };

    ws.current.onmessage = (event) => {
      const message = JSON.parse(event.data);

      if (message.channel && message.channel.startsWith("kline")) {
        const k = message.data;

        const candle = {
          x: new Date(k.t),     // open time
          o: parseFloat(k.o),
          h: parseFloat(k.h),
          l: parseFloat(k.l),
          c: parseFloat(k.c),
        };

        setCandles((prevCandles) => {
          const last = prevCandles[prevCandles.length - 1];
          if (last && last.x.getTime() === candle.x.getTime()) {
            return [...prevCandles.slice(0, -1), candle];
          } else {
            return [...prevCandles, candle];
          }
        });
      }
    };

    return () => {
      ws.current?.close();
    };
  }, [symbol, interval]);

  return candles;
}
