import React, { useState, useEffect } from "react";
import { WalletInfo } from "./components/WalletInfo";
import { Chart } from "./components/Chart";
import { TradeForm } from "./components/TradeForm";
import { OpenOrders } from "./components/OpenOrders";
import { OrderHistory } from "./components/OrderHistory";
import { useMexc } from './hooks/useMexc';
import { AlgorithmSettings } from './components/AlgorithmSettings';


function App() {
  const {
    wallets,
    selectedWallet,
    setSelectedWallet,
    addWallet,
    removeWallet,
    showAddModal,
    setShowAddModal
  } = useWallet();

  const {
    balance,
    currentPrice,
    priceHistory,
    openOrders,
    orderHistory,
    connectWallet,
    placeOrder,
    setLeverage,
    applyAlgorithm,
    algorithmSettings,
    updateAlgorithmSettings,
    setSelectedAlgorithm,
    connectionStatus
  } = useMexc();

  // Seçili cüzdan değiştiğinde bağlantıyı güncelle
  useEffect(() => {
    if (selectedWallet) {
      connectWallet(selectedWallet.apiKey, selectedWallet.secretKey);
    }
  }, [selectedWallet]);

  const handleAddWallet = (wallet) => {
    addWallet(wallet);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-6">
      {/* Add Wallet Modal */}
      <AddWalletModal
        show={showAddModal}
        onClose={() => setShowAddModal(false)}
        onAdd={handleAddWallet}
      />

      <header className="flex justify-between items-center mb-6">
        <h1 className="text-2xl md:text-3xl font-bold text-gray-800">Doge/USDT Trading Bot</h1>

        {/* Wallet Selector */}
        <div className="w-64">
          <WalletSelector
            wallets={wallets}
            selectedWallet={selectedWallet}
            setSelectedWallet={setSelectedWallet}
            setShowAddModal={setShowAddModal}
          />
        </div>
      </header>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
        {/* Left Column */}
        <div className="space-y-6">
          <div className="bg-white rounded-2xl shadow p-6">
            <WalletInfo balance={balance} connectWallet={connectWallet} />
          </div>
          <div className="bg-white rounded-2xl shadow p-6">
            <TradeForm
              currentPrice={currentPrice}
              placeOrder={placeOrder}
              algorithm={algorithm}
              setAlgorithm={setAlgorithm}
              leverage={leverage}
              setLeverageInput={setLeverageInput}
              applyAlgorithm={applyAlgorithm}
              connectWallet={connectWallet}
            />
          </div>
        </div>

        {/* Center Column - Full width on small screens, double on large */}
        <div className="lg:col-span-2 bg-white rounded-2xl shadow p-6">
          <Chart priceHistory={priceHistory} currentPrice={currentPrice} />
        </div>

        {/* Bottom Section - Full width */}
        <div className="lg:col-span-3 space-y-6">
          <div className="bg-white rounded-2xl shadow p-6">
            <OpenOrders orders={openOrders} currentPrice={currentPrice} />
          </div>
          <div className="bg-white rounded-2xl shadow p-6">
            <OrderHistory history={orderHistory} />
          </div>
        </div>
        <div className="settings-section">
        <AlgorithmSettings
          algorithmSettings={algorithmSettings}
          updateAlgorithmSettings={updateAlgorithmSettings}
          setSelectedAlgorithm={setSelectedAlgorithm}
        />
      </div>
      </div>
    </div>
  );
}

export default App;
