import React from 'react';
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  Title,
  Tooltip,
  CategoryScale,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

// Chart bileşenlerini kaydet
ChartJS.register(LineElement, PointElement, LinearScale, Title, Tooltip, CategoryScale);

export const Chart = ({ priceHistory, currentPrice, loading }) => {
  const data = {
    labels: priceHistory.map((_, i) => i),
    datasets: [{
      label: 'DOGE/USDT Price',
      data: priceHistory,
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.1
    }]
  };

  const options = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: function (context) {
            return `Price: ${context.raw.toFixed(8)} USDT`;
          }
        }
      }
    },
    scales: {
      y: {
        ticks: {
          callback: function (value) {
            return parseFloat(value).toFixed(8);
          }
        }
      }
    }
  };

  return (
    <div className="relative h-[400px] bg-white rounded-lg shadow p-4">
      {loading ? (
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="animate-pulse text-gray-500">Loading data...</div>
        </div>
      ) : (
        <>
          <div className="absolute top-5 left-5 z-10 bg-white/90 rounded-lg shadow-sm p-3">
            <h3 className="font-bold text-gray-700">DOGE/USDT</h3>
            <div className="text-2xl font-bold">
              {currentPrice.toFixed(8)} USDT
            </div>
          </div>
          <Line data={data} options={options} />
        </>
      )}
    </div>
  );
};
