// src/components/CandleChart.jsx
import React from 'react';
import {
  Chart,
  TimeScale,
  LinearScale,
  CandlestickController,
  CandlestickElement,
  Tooltip,
  Legend
} from 'chart.js'; 
import 'chartjs-adapter-date-fns';
import { FinancialController  } from 'chartjs-chart-financial';

Chart.register(
  TimeScale,
  LinearScale,
  CandlestickController,
  CandlestickElement,
  Tooltip,
  Legend
);

export const CandleChart = ({ priceData, interval, onIntervalChange }) => {
  const data = {
    datasets: [{
      label: 'DOGE/USDT',
      data: priceData,
      borderColor: 'rgba(0, 0, 0, 1)',
      borderWidth: 1
    }]
  };

  const options = {
    responsive: true,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: interval === '1d' ? 'day' : 'hour',
          tooltipFormat: 'yyyy-MM-dd HH:mm',
        },
        title: { display: true, text: 'Time' }
      },
      y: {
        title: { display: true, text: 'Price (USDT)' }
      }
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: context => {
            const ohlc = context.raw;
            return `O: ${ohlc.o}, H: ${ohlc.h}, L: ${ohlc.l}, C: ${ohlc.c}`;
          }
        }
      }
    }
  };

  return (
    <div>
      <div style={{ marginBottom: 10 }}>
        {['15m', '1h', '4h', '1d'].map(i => (
          <button key={i} onClick={() => onIntervalChange(i)} style={{ marginRight: 8 }}>
            {i}
          </button>
        ))}
      </div>
      <Chart type='candlestick' data={data} options={options} />
    </div>
  );
};
